package Association;

public class Bid {

	private int id;
	private int Amount;
	private int ItemId;

	public Bid() {
		// TODO Auto-generated constructor stub
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public int getAmount() {
		return Amount;
	}

	public void setAmount(int string) {
		Amount = string;
	}

	public int getItemId() {
		return ItemId;
	}

	public void setItemId(int itemId) {
		ItemId = itemId;
	}

}
